INSERT INTO `states` VALUES (1, 57, 1, 'Azuay', 'Cuenca', 'Es llamada la Atenas del Ecuador por su arquitectura, su diversidad cultural, su aporte a las artes, ciencias y letras ecuatorianas y por ser el lugar de nacimiento de muchos personajes ilustres de la sociedad ecuatoriana', '569.42', '122.00', '-2.902222', '-79.005261', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (2, 57, 1, 'Bolivar', 'Guaranda', 'Bolívar es una provincia del centro de Ecuador, en la cordillera occidental de los Andes. Su capital es la ciudad de Guaranda. La Provincia de Bolívar se llama así en honor al Libertador Simón Bolívar.', '183 641', '3254.00', '-1.6', '-79', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (3, 57, 1, 'Cañar', 'Azoguez', 'La provincia destaca como uno de los sitios turísticos más importantes del país, destacándose entre otros la Fortaleza de Ingapirca, la Laguna de Culebrillas y la ciudad de Azogues.', '33848.00', '3908.00', '-2.733333', '-78.833333', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (4, 57, 1, 'Carchi', 'Tulcán', 'Carchi es una provincia ecuatoriana situada al norte del Ecuador en la frontera con Colombia. Su capital es la ciudad de Tulcán. Forma parte de la región 1', '82734.00', '3783.00', '0.811667', ' -77.718611', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (5, 57, 1, 'Chimborazo', 'Riobamba', 'En esta provincia se encuentran varias de las cumbres más elevadas del país, como el Carihuairazo, el Altar, Igualata, Sangay, entre otros, que en algunos casos comparte con otras provincias.', '223586.00', '6487.00', '-1.666667', '-78.65', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (6, 57, 1, 'Cotopaxi', 'Latacunga', 'La provincia toma el nombre del volcán más grande e importante de su territorio, el volcán Cotopaxi. Cotopaxi se encuentra dividida políticamente en 7 cantones. Según el último ordenamiento territorial, la provincia de Cotopaxi pertenece a la región centro 3', '409 205', '6569.00', '-0.933333', ' -78.616667', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (7, 57, 2, 'El Oro', 'Machala', 'La capital de la provincia de El Oro es la ciudad de Machala, quinta ciudad del país, considerada como la  capital bananera del mundo .', '260528.00', '6188.00', '-3.266669', ' -79.9667', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (8, 57, 2, 'Esmeraldas', 'Esmeraldas', 'Provincia del Ecuador situada en su costa noroccidental, conocida popularmente como la provincia verde. Sucapital homónima es uno de los puertos principales del Ecuador y terminal del oleoducto transandino. Posee un aeropuerto para vuelos nacionales e internacionales', '189504.00', '15 954', '-0.966667', ' -79.65', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (9, 57, 3, 'Galápagos', 'P. Baquerizo Moreno', 'Es el mayor centro turístico del Ecuador, así como también una de las reservas ecológicas más grandes e importantes del planeta. Con sus 26.640 habitantes, Galápagos es la provincia menos poblada del país, debido principalmente al afán de conservar al máximo la flora y fauna de la región.', '5600.00', '8010.00', '-0.666667', ' -90.55', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (10, 57, 2, 'Guayas', 'Guayaquil', 'Es el mayor centro comercial e industrial del Ecuador. Con sus 3,8 millones de habitantes, Guayas es la provincia más poblada del país, constituyéndose con el 24,5% de la población de la República.', '2526927.00', '17139.00', '-2.2', ' -79.9667', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (11, 57, 1, 'Imbabura', 'Ibarra', 'La provincia también es conocida por sus contrastes poblacionales es así que la población está marcada por diferentes factores demográficos, además desde siempre ha sido núcleo de artesanías y cultura.', '181722.00', '4599.00', '0.35', '-78.133333', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (12, 57, 1, 'Loja', 'Loja', 'Loja es una ciudad del Ecuador, capital de la provincia y cantón Loja, tiene una rica tradición en las artes, y por esta razón es conocida como la capital musical y cultural del Ecuador.', '206.83', '57.00', '-3.833', '-80.067', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (13, 57, 2, 'Los Rios', 'Babahoyo', 'Los Ríos, oficialmente Provincia de Los Ríos, es una de las 24 provincias de la República del Ecuador, localizada en la Región Costa del país. Su capital es la ciudad de Babahoyo y su localidad más poblada es la ciudad de Quevedo. Es uno de los más importantes centros agrícolas del Ecuador. Su territorio está ubicado en la parte central del litoral del país y limita con las provincias de Guayas, Santo Domingo de los Tsáchilas, Manabí, Cotopaxi y Bolívar. Según el último ordenamiento territorial, la provincia de Los Ríos pertenece a la región comprendida también por las provincias de Bolívar, Guayas y Santa Elena.', '778115.00', '6254.00', '-1.766669', '-79.45', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (14, 57, 2, 'Manabí', 'Portoviejo', 'Manabí es una provincia ecuatoriana localizada en el emplazamiento centro-noroeste del Ecuador continental, cuya unidad jurídica se ubica en la región geográfica del litoral, que a su vez se encuentra dividida por el cruce de la línea equinoccial. Su capital es Portoviejo. Limita al oeste con el Océano Pacífico, al norte con la provincia de Esmeraldas, al este con la provincia deSanto Domingo de los Tsáchilas y Los Ríos, al sur con la provincia de Santa Elena y al sur y este con la provincia de Guayas. Con una población de 1,395.249 habitantes, es la tercera provincia más poblada de Ecuador.', '1369780.00', '18400.00', '-1.052219', '-80.4506', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (15, 57, 4, 'Morona Santiago', 'Macas', 'Morona Santiago (nombre oficial Provincia de Morona Santiago) es una de las 24 provincias que conforman la República del Ecuador. Es una provincia de la Amazonía ecuatoriana. Su capital es la ciudad de Macas, la cual además, es su urbe más poblada. Su territorio limita al norte con Pastaza, al sur con Zamora Chinchipe, al este con Perú, y al oeste con Tungurahua, Azuay, Chimborazo y Cañar. Ocupa una superficie de 25.690 km², un área similar a la de Cerdeña.', '147940.00', '25690.00', '-2.366667', ' -78.133333', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (16, 57, 4, 'Napo', 'Tena', 'La provincia de Napo es una de las provincias de la Región Centro Norte (Ecuador), de la República del Ecuador, situada en la región amazónica ecuatoriana e incluyendo parte de las laderas de los Andes, hasta las llanuras amazónicas. Toma su nombre del río Napo. Su capital es la ciudad de Tena. Limita al norte con Sucumbíos, al sur con Pastaza, al oeste con Pichincha,Cotopaxi y Tungurahua y al este con la Provincia de Orellana.', '103697.00', '13271.00', '0.989', '-77.8159', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (17, 57, 4, 'Orellana', 'Francisco de Orellana', 'Orellana, provincia de la Región Centro Norte (Ecuador), Ecuador, La capital de la provincia es El Coca más conocida como «Coca». Al norte limita con Sucumbíos, al sur con la provincia de Pastaza, al este con Perú y al oeste con Napo. Tiene una superficie de 20.733 km², que en términos de extensión es similar a la de El Salvador. Es una de las provincias más nuevas del país, provincia 22, pues fue creada en 1998 al ser separada de Napo. Inicialmente su nombre iba a ser provincia de Amazonaspero ante un potencial reclamo de Perú se decidió denominarla Orellana. La población es de 136.396 habitantes; sus habitantes nativos pertenecen principalmente a las nacionalidades waorani, schuar y kichwa.', '136396.00', '20773.00', '-0.933333', '-75.666667', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (18, 57, 4, 'Pastaza', 'Puyo', 'Pastaza, oficialmente Provincia de Pastaza, es una de las 24 provincias que conforman la República del Ecuador, situada en laRegión Amazónica del Ecuador. Recibe su nombre del río Pastaza, que la separa al sur de la provincia de Morona Santiago. Su capital es la ciudad de Puyo.', '83933.00', '29520.00', '-1.066667', '-78.001111', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (19, 57, 1, 'Pichincha', 'Quito', 'La Provincia de Pichincha es una de las 24 provincias que conforman la República del Ecuador. Se encuentra ubicada al norte del país, en la zona geográfica conocida como sierra. Su capital administrativa es la ciudad de Quito, la cual además es su urbe más poblada y la capital del país. Es también el principal centro comercial del país.', '2576287.00', '9612.00', '-0.25', '-78.583333', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (20, 57, 2, 'Santa Elena', 'Santa Elena', 'Santa Elena es una provincia de la costa de Ecuador creada el 7 de noviembre de 2007, la más reciente de las 24 actuales, con territorios que anterior a esa fecha formaban parte de la provincia del Guayas, al oeste de ésta.', '308693.00', '3763.00', '-2.2267', ' -80.8583', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (21, 57, 2, 'Santo Domingo de los Tsáchilas', 'Santo Domingo', 'La Provincia de Santo Domingo de los Tsáchilas es una de las provincias de la República del Ecuador y forma parte de laRegión Costa, históricamente conocida como Provincia de Yumbos. Su territorio está en zona de trópico húmedo. La provincia toma su nombre de los Tsáchilas, etnia ancestral de la zona.', '410937.00', '4180.00', '-0.333333', '-79.25', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (22, 57, 4, 'Sucumbios', 'Nueva Loja', 'Sucumbíos es una provincia del nor-oriente del Ecuador. Su capital es Nueva Loja. Es una de las principales provincias que proveen al Estado ecuatoriano del petróleo que necesita para las exportaciones. Se caracteriza por sus bellos paisajes amazónicos, entre ellos el río Aguarico.', '176472.00', '18612.00', '-0.083333', '-76.883333', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (23, 57, 1, 'Tungurahua', 'Ambato', 'Tungurahua, oficialmente Provincia de Tungurahua, es una de las 24 provincias que conforman la República del Ecuador. Se encuentra al centro del país, en la región geográfica conocida como sierra. La ciudad de Ambato es su capital administrativa; se divide en 9 cantones. Forma parte de la región comprendida también por las provincias de Cotopaxi, Chimborazo y Pastaza.', '504583.00', '3334.00', '-1.233333', '-78.616667', 1, NULL, NULL, NULL);
INSERT INTO `states` VALUES (24, 57, 4, 'Zamora Chimchipe', 'Zamora', 'Zamora Chinchipe es una provincia de Ecuador ubicada en el sur-oriente de la Amazonía ecuatoriana, que limita con la provincia de Morona Santiago al norte; con la provincia de Loja al oeste; y con Perú al sur y este. Según el último ordenamiento territorial del Ecuador, Zamora Chinchipe pertenece a la Región Sur comprendida también por las provincias de Loja y El Oro. Tiene una superficie de 10.556 km², la cual comprende una orografía montañosa única que la distingue del resto de provincias amazónicas.', '91376.00', '10556.00', '-2.883333', '-79', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (1, 1, 'Cuenca', 'El cantón Cuenca es una entidad territorial subnacional ecuatoriana, de la Provincia de Azuay. Con 505.585 habitantes, según las estadísticas para el 2010, es el tercer cantón más poblado del Ecuador. Su cabecera cantonal es la ciudad deCuenca, lugar donde se agrupa gran parte de su población total.', '505585', '3086.00 ', '2.9', '78.98333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (2, 1, 'Girón', 'Girón es un cantón de la provincia de Azuay en Ecuador, ubicado 36 km al sur de la capital provincial.', '14953', '0.00 ', '3.159166667', '79.1475', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (3, 1, 'Gualaceo', 'Gualaceo cantón altivo y señorial, debe su riqueza a la diversidad cultural y de razas entremezcladas con el paso del tiempo, así podríamos hablar de un origen mítico con la leyenda de las Guacamayas, que dieron paso a la tribu Cañari, los primeros ayllus, los conquistadores incas y luego los colonos españoles.', '38587', '0.00 ', '2.89', '78.77944444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (4, 1, 'Nabón', 'Nabón es un cantón de la provincia del Azuay (Ecuador) de 668,2 km², ubicado al sudeste, una de sus características geográficas es la dispersión de su territorio, lo que impide un fácil acceso a las diferentes comunidades y dificulta la comunicación entre ellas.', '15121', '668.20 ', '3.34', '79.06416667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (5, 1, 'Paute', 'Paute es un cantón localizado en la provincia de Azuay en Ecuador. Fue fundado el 26 de febrero de 1860. En el censo de 2010 tenía 25.494 habitantes. Se encuentra al noreste de Cuenca 40 minutos.', '25494', '0.00 ', '2.78', '78.76111111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (6, 1, 'Pucara', 'Pucará es un cantón de la provincia del Azuay, Ecuador a 126 km de la ciudad de Cuenca, capital de la provincia, según el censo de 2010 tiene una población de 10.052 habitantes.1', '10052', '0.00 ', '2.78', '78.76111111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (7, 1, 'San Fernando', 'A 62 km de la capital provincial, Cuenca, el cantón tiene una población total de 3.931 habitantes, de los cuales 3.256 se encuentran en zonas rurales.Su altitud es de 2.600 metros sobre el nivel del mar, su clima es templado. Los meses más fríos van de enero a marzo, cuando son frecuentes las heladas.', '3931', '0.00 ', '3.147777778', '79.25166667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (8, 1, 'Santa Isabel', 'Santa Isabel cantón occidental de la provincia del Azuay en Ecuador, se localiza en la cuenca alta y media del río Jubones, su clima es variado y presenta oscilaciones entre 8ºC y 24ºC, este cantón va desde los 800 hasta los 2400 msnm, lo cual presenta grandes zonas para la biodiversidad. Su extensión territorial es de 771.42km² siendo el 9.63% de la provincia del Azuay', '18393', '771.42 ', '3.274166667', '79.31555556', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (9, 1, 'Sigsig', 'Es parte del nudo vial Sigsig Matanga, que comunica a Azuay con Gualaquiza. Sus actividades principales, son la agricultura y la artesanía. Existe un complejo arqueológico muy importante llamado Chobshi.', '26910', '0.00 ', '3.053055556', '78.79333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (10, 1, 'Oña', 'San Felipe de Oña cantón meridional de la provincia de Azuay en Ecuador, a 120km de la capital provincial Cuenca, tiene una población total del cantón de 3234 habitantes, de los cuales 2325 hab, se encuentran en zonas rurales, tiene una topografía muy irregular.', '3234', '298.00 ', '3.469722222', '79.15416667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (11, 1, 'Chordeleg', 'Chordeleg es un cantón en la provincia del Azuay, en el Ecuador. La fecha de cantonización es el 15 Abril de 1992. Es famoso por su artesanía, como cerámicas, bordados a mano y joyería, cuenta con decenas de joyerías en las cuales se destacan los maravillosos trabajos de orfebrería de sus habitantes.', '10859', '104.70 ', '2.933333333', '78.76666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (12, 1, 'El pan', 'El Pan es un cantón de la provincia del Azuay, Ecuador. En el censo del año 2001 tenía 3.075 habitantes. Su fecha de creación es el 10 de agosto de 1992. Se encuentra al noreste de la ciudad de Cuenca (Ecuador) a 60 kilómetros, el centro cantonal tiene una altitud de 2250 msnm.', '3036', '0.00 ', '2.786111111', '78.665', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (13, 1, 'Sevilla de Oro', 'La Parroquia Urbana de Sevilla de Oro, en sus primeros años se lo conocía con el nombre “Chimbapan”, que en quechua quiere decir banda de El Pan.', '5889', '311.00 ', '2.316666667', '78.1', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (14, 1, 'Guachapala', 'Guachapala es un cantón de la provincia del Azuay, Ecuador, fue creado en la presidencia del Arquitecto Sixto Durán Ballén de acuerdo con el suplemento del registro oficial No. 623 del 31 de enero de 1995', '3409', '41.07 ', '2.770555556', '78.71222222', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (15, 1, 'Camilo Ponce Enriquez', 'Camilo Ponce Enríquez es un cantón de la provincia de Azuay, Ecuador. Se encuentra en la región costanera de la provincia a 3 horas de la ciudad de Cuenca (Ecuador).', '21998', '639.30 ', '3.05', '79.73333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (16, 2, 'Guaranda', 'El cantón Guaranda es una entidad territorial subnacional ecuatoriana, de la Provincia de Bolívar. Se ubica en el centro de laRegión Sierra. Su cabecera cantonal es la ciudad de Guaranda, lugar donde se agrupa gran parte de su población total.', '91877', '1898.00 ', '1.605555556', '79.00305556', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (17, 2, 'Chillanes', 'El cantón Chillanes está ubicado en la provincia de Bolívar, Ecuador. Limita al norte con el cantón San Miguel. Al sur con el cantónGeneral Elizalde (Bucay) provincia del Guayas, al Oeste Cantón Babahoyo Provincia del Los Ríos y al este el Cantón Pallatanga Provincia del Chimborazo.', '17406', '568. 22 ', '1.942777778', '79.06666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (18, 2, 'Echeandia', 'Está ubicado al occidente de la provincia, en las estribaciones de la cordillera. Climáticamente, pertenece a la zona subtropical, lo cual favorece su economía, ya que la mayor parte de la población se dedica a la agricultura, con la producción de la zona: naranjas, café, caco, caña de azúcar.', '12114', '230.62 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (19, 2, 'San Miguel', 'San Miguel de Bolívar, Ecuador. se encuentra 20 km al sur de Guaranda, en la parte central de la provincia. Está a una altura entre de 2.500 y 3.500 msnm. Tiene una temperatura promedio de 17,5 ºC.', '27244', '0.00 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (20, 2, 'Caluma', 'Caluma, es la ciudad principal del cantón del mismo nombre de la provincia de Bolívar, de la república del Ecuador. Goza de un excelente clima y con un entorno natural extraordinario para quienes aman la naturaleza y los deportes extremos, su vegetación es exuberante al igual que su fauna, cuenta con decenas de cascadas, ríos y cultivos de naranja que es considerada como la mejor del Ecuador ya por su delicioso y único sabor, cacao, mandarina, entre otras especies exóticas.', '13129', '176.50 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (21, 2, 'Las naves', 'Sus tierras ofrecen una variedad de recursos para el ecoturismo. Este cantón es eminentemente agrícola, produce: naranja, sandía, zapote, aguacate, pina, mango, guaba, arroz, plátano, cacao, café, maíz, fríjol, verduras, etc.', '6092', '0.00 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (22, 3, 'Azogues', 'Azogues o San Francisco de Peleusí de Azogues, es una ciudad del Ecuador, capital de la provincia de Cañar.', '70064', '60.94 ', '2.733333333', '78.83333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (23, 3, 'Biblian', 'Cantón de la provincia de Cañar ubicado en la zona septentrional de la hoya del Paute, a una altura media de 2608 msnm. El clima es frío y húmedo, con una temperatura media de 14°C.', '20817', '205.30 ', '2.716666667', '78.88333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (24, 3, 'Cañar', 'El cantón Cañar es una entidad territorial subnacional ecuatoriana, de la Provincia de Cañar. Su cabecera cantonal es la ciudad de Cañar, lugar donde se agrupa gran parte de su población total. Es considerada Capital Arqueólogica del Ecuador, por el complejo de Ingapirca.', '59323', '1804.00 ', '2.483611111', '78.97833333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (25, 3, 'La Troncal', 'Este pequeño poblado a surgido por la agricultura de caña de azúcar cuyo icono la caracteriza, alberga a uno de los ingenios azucareros mas productivos del país, como la principal industria de la zona y es generadora de cientos de plazas de trabajo para beneficio económico de los habitantes del sector.', '54389', '324.00 ', '2.4', '79.33333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (26, 3, 'El tambo', 'El Tambo es uno de los siete cantones de la Provincia de Cañar. Está situada en la Carretera Panamericana, a seis kilómetros del cantón Cañar, a una altura de 2983msnm. La población es mestiza e indígena.', '9475', '564.00 ', '2.511111111', '78.92833333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (27, 3, 'Deleg', 'Cantón de la provincia de Cañar La zona fue originalmente un asentamiento de mitimaes, esto se explica por el aniquilamiento de los cañaris realizado por Atahualpa, por haber ayudado a Huáscar en la guerra civil inca.', '6100', '80.00 ', '2.788055556', '78.92611111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (28, 3, 'Suscal', 'Su significado, en lengua Quichua, seria:  Abundancia de neblina . Esta denominación se debe a que el cantón Suscal efectivamente es un lugar situado en medio de imponentes montañas, de un paisaje solemne y en donde la neblina es frecuente', '5016', '30.62 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (29, 4, 'Tulcan', 'El cantón Tulcán es una entidad territorial subnacional ecuatoriana, de la Provincia de Carchi. Se encuentra ubicada en los Andes Septentrionales del Ecuador en la frontera con Colombia. Su cabecera cantonal es la ciudad de Tulcán, lugar donde se agrupa gran parte de su población total.', '86498', '1801.00 ', '0.25', '78.58333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (30, 4, 'Bolivar', 'Bolívar es un cantón ubicado en la parte centro noreste de la provincia de Manabí, Ecuador. Limita al este con el cantónPichincha, al sur con los cantones Portoviejo y Junín, al norte con los cantones Tosagua y Chone. Su extensión es de aproximadamente 600 km²', '14347', '600.00 ', '0.841944444', '80.16194444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (31, 4, 'Espejo', 'Espejo es un cantón de la provincia de Carchi en el Ecuador, cuya cabecera es la población El Ángel, zona privilegiada por su contraste geográfico de parajes hermosos que dan un toque de misterio al turista que busca aventura, Espejo herencia cultural, tesoro escondido de la Provincia.', '13364', '344.51 ', '0.621111111', '77.94', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (32, 4, 'Mira', 'Mira es un cantón en la provincia de Carchi en el norte del Ecuador. Tiene una población aproximada de 12.919 con una extensión territorial de 581,69 Km2 y una temperatura promedio de 16.2°C, se encuentra al sur de la misma provincia cercana a la ciudad de Ibarra, y cercano a Salinas en Imbabura.', '12180', '581.69 ', '0.553888889', '78.04916667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (33, 4, 'Montufar', 'Se le conocía como TUSA, pero se cambia a San Gabriel en honor al arcángel San Gabriel Guardián y protector de la ciudad de ahí toma el nombre la capital de Montúfar en el año 1883.', '30511', '344.51 ', '0.616666667', '77.83333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (34, 4, 'San Pedro de Huaca', 'San Pedro de Huaca es un cantón de la provincia de Carchi, en Ecuador. Su capital es la localidad de Huaca. Su población en el censo de 20102 es de 7.624 habitantes. Se encuentra a una altitud de 2923msnm.', '7624', '344.51 ', '0.630277778', '77.72666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (35, 5, 'Riobamba', 'El cantón Riobamba es una entidad territorial subnacional ecuatoriana, de la Provincia de Chimborazo. Su cabecera cantonales la ciudad de Riobamba, lugar donde se agrupa la mayor parte de su población total.', '263412', '990.00 ', '1.674444444', '78.64833333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (36, 5, 'Alausi', 'San Pedro de Alausí es la capital del Cantón Alausí, está ubicada en la cordillera occidental. Fue fundada el 29 de junio de 1534 y se considera fue la primera población en la Real Audiencia de Quito.', '44089', '1.64 ', '2.202777778', '78.84611111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (37, 5, 'Colta', 'Colta es un cantón de la Provincia de Chimborazo en el Ecuador. Se sitúa en una altitud promedio de 3.212 msnm y es considerada una de las ciudades más altas del País. La temperatura media es de 12 °C. Su proximidad a la ciudad de Riobamba, está a solo 18 km, hace de ella una ciudad turística importante.', '0', '850 .', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (38, 5, 'Chambo', 'Chambo es un cantón de la Provincia de Chimborazo en el Ecuador. Está ubicado el noroeste de la provincia. Posee una superficie de 163 km2, que representan el 2.5% de la superficie de la provincia de Chimborazo. Los principales cursos de agua que cruzan el cantón son los ríos Chambo, Daldal y Timbul. Cuenta además con la laguna Rocón.', '11885', '162.00 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (39, 5, 'Chunchi', 'El cantón Chunchi pertenece a la Provincia de Chimborazo en el Ecuador. Está ubicado en el extremo sur de la provincia. Chunchi es conocido también como el Sillón Andino del Ecuador.', '12686', '279.00 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (40, 5, 'Guamote', 'Guamote es un cantón de la Provincia de Chimborazo en el Ecuador. Se sitúa en una altitud promedio de 3.050 msnm . La temperatura media es de 12 °C.', '45153', '520.00 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (41, 5, 'Guano', 'Guano es un cantón de la Provincia de Chimborazo en el Ecuador. Tiene una superficie de 473 km2, y su rango de altitud va desde los 2.000 hasta los 6.310 msnm, en el nevado Chimborazo. La cabecera cantonal está situada a diez minutos de Riobamba.', '42851', '473.00 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (42, 5, 'Pallatanga', 'Pallatanga es un cantón de la Provincia de Chimborazo en el Ecuador. Se ubica a más o menos 1 hora y 30 minutos de la capital de la provincia, Riobamba, y a 3 horas desde Guayaquil.', '11544', '270.00 ', '1.997777778', '78.96555556', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (43, 5, 'Penipe', 'Penipe es un cantón de la Provincia de Chimborazo en el Ecuador. Se sitúa entre los 2.500 y los 5.424 msnm, en el nevado El Altar. Se encuentra ubicado en el noreste de la provincia, a 22 km de distancia de la ciudad de Riobamba, con una extensión territorial de 240 km2. Su latitud es 1° 34  sur y su longitud 78° 31  60   oeste.', '6739', '240.00 ', '1.566666667', '8.533333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (44, 5, 'Cumanda', 'Cumandá es un cantón de la Provincia de Chimborazo en el Ecuador, se encuentra ubicado al sur oeste de la provincia de Chimborazo, a unas 2,45 horas de Riobamba. Fue cantonizado el 28 de enero de 1992. La cabecera cantonal, Cumandá, está situada a orillas del río Chimbo, que la separa del vecino centro poblado de Bucay, perteneciente a la Provincia del Guayas.', '12922', '158.70 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (45, 6, 'Latacunga', 'El cantón Latacunga es una entidad territorial subnacional ecuatoriana, de la Provincia de Cotopaxi. Su cabecera cantonales la ciudad de Latacunga, lugar donde se agrupa gran parte de su población total.', '170489', '1377.00 ', '0.933333333', '78.61666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (46, 6, 'La Maná', 'La Maná, es uno de los siete cantones de la Provincia de Cotopaxi,. Tiene una superficie total de 66.258 hectáreas. Es el sexto cantón de la provincia de Cotopaxi.', '42216', '398.00 ', '0.933333333', '79.21666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (47, 6, 'Pangua', 'El Cantón Pangua, en la Provincia de Cotopaxi, en el centro del Ecuador, se formó el 31 de mayo de 1938.', '21965', '714.90 ', '-1.2', '-79.1', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (48, 6, 'Pujili', 'El cantón Pujilí es una entidad territorial subnacional ecuatoriana, de la Provincia de Cotopaxi. Su cabecera cantonal es la ciudad de Pujilí, lugar donde se agrupa gran parte de su población total. La cabecera cantonal se encuentra a 2.961 msnm, en las laderas del monte Sinchahuasín. Se fundó en 1657.', '69055', '1305.20 ', '0.95', '78.68333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (49, 6, 'Salcedo', 'El cantón Salcedo es una entidad territorial subnacional ecuatoriana, de la Provincia de Cotopaxi. Su cabecera cantonal es la ciudad de Salcedo, lugar donde se agrupa gran parte de su población total. Se cantonizó el 19 de septiembre de 1919. Su nombre es en honor al orador Manuel Antonio Salcedo y Legorburú, sacerdote.', '58216', '484.00 ', '1.05', '78.58333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (50, 6, 'Saquisili', 'El cantón Saquisilí está situado en la Provincia de Cotopaxi, sierra central del Ecuador. El cantón se sitúa entre las cotas 2.900 y 4.200 msnm. La temperatura media es de 12 °C.', '25320', '1.29 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (51, 6, 'Sigchos', 'Sigchos, es uno de los siete cantones de la Provincia de Cotopaxi, en el centro del Ecuador. La cantonización se produjo el 21 de julio de 1992.', '21944', '0.00 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (52, 7, 'Machala', 'El cantón Machala es una entidad territorial subnacional ecuatoriana, de la Provincia de El Oro. Su cabecera cantonal es la ciudad de Machala, lugar donde se agrupa gran parte de su población total.', '245972', '338.00 ', '3.266666667', '79.96666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (53, 7, 'Arenillas', 'Arenillas, oficialmente Cantón Arenillas, es un pequeño cantón ubicado en la parte Sur-occidental del territorio ecuatoriano y de la provincia de El Oro. Su capital es la ciudad de Arenillas, que de acuerdo al censo de 2001 tenía unapoblación total de 22 477 habitantes.', '26844', '810.17 ', '3.55', '80.06666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (54, 7, 'Atahualpa', 'El cantón ecuatoriano Atahualpa, se encuentra ubicado en el centro geográfico de la provincia de El Oro, en Ecuador. Recibe su nombre en honor al último inca, Atahualpa.', '5833', '0.00 ', '2.9', '78.93333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (55, 7, 'Balsas', 'Balsas es un cantón de la Provincia de El Oro en el Ecuador. El nombre del cantón se debe a la abundante cantidad de árboles de balsa que existían en este sector. Las costumbres y tradiciones en Balsas se da en base a un arrastre que trajeron los primeros pobladores procedentes del Cantón Piñas, Zaruma y parte de la provincia de Loja.', '6861', '0.00 ', '-3.7626', '-79.8253', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (56, 7, 'Chilla', 'El origen del nombre de Chilla, se da en razón del viento que sopla en la colina y produce un sonido fuerte que imita a un chillido. Chilla estuvo situada inicialmente en lo que hoy se conoce por Pueblo Viejo, recinto cercano al actual poblado de Chilla.', '2484', '328.00 ', '-3', '-79.58', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (57, 7, 'El Guabo', 'El Guabo es un cantón de la Provincia de El Oro en Ecuador. Su cabecera cantonal es la ciudad de El Guabo. Su población en el censo de 2010 es de 50.009 habitantes.1', '50009', '4982.00 ', '-3', '-79.8292', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (58, 7, 'Huaquillas', 'Huaquillas es la ciudad fronteriza y principal entrada vía terrestre al Ecuador, ubicada al suroeste de la provincia ecuatoriana deEl Oro. Es la cuarta ciudad con mayor número de habitantes en la provincia.', '48285', '72.00 ', '3.481388889', '80.24333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (59, 7, 'Marcabeli', 'El Cantón Marcabelí es una municipalidad de la provincia de El Oro. Su cabecera cantonal es la ciudad de Marcabelí. Su población es de 5.450 habitantes,1 tiene una superficie de 147km2.2 Su alcalde actual para el período 2009 - 2014 es Jorge Carrión Mora.', '5450', '1472.20 ', '3.786111111', '79.89777778', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (60, 7, 'Pasaje', 'El cantón Pasaje es una entidad territorial subnacional ecuatoriana, de la Provincia de El Oro. Se ubica en la Región Costa. Su cabecera cantonal es la ciudad de Pasaje, lugar donde se agrupa gran parte de su población total...', '72806', '451.00 ', '3.326944444', '79.805', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (61, 7, 'Piñas', 'Piñas es un cantón de la Provincia de El Oro que se encuentra al suroeste de Ecuador. Limita con los cantones: Balsas,Marcabelí, Arenillas, Santa Rosa, Atahualpa, Zaruma, y Portovelo pertenecientes a su misma provincia; y además limita con laProvincia de Loja.', '25988', '571.10 ', '3.666666667', '79.65', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (62, 7, 'Portovelo', 'Portovelo es un cantón de la Provincia de El Oro, en Ecuador. Recibió su nombre en la época de la conquista española por su semejanza con otros pueblos de nombre similar en Panamá en los que buscaban oro, siendo además la orografía muy semejante.', '12200', '286.20 ', '3.716666667', '79.61666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (63, 7, 'Santa rosa', 'El cantón Santa Rosa es una entidad territorial subnacional ecuatoriana, de la Provincia de El Oro. Su cabecera cantonal es la ciudad de Santa Rosa, lugar donde se agrupa gran parte de su población total. Su población es de 69.036 habitantes,1tiene una superficie de 889km2.2 Su alcalde actual para el período 2009 - 2014 es Clemente Bravo.', '69036', '8892.00 ', '3.452222222', '79.96166667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (64, 7, 'Zaruma', 'En 1990, el Ministerio de Educación y Cultura de Ecuador declara a Zaruma Patrimonio Cultural del Estado Ecuatoriano. Desde 1998 Zaruma consta en la lista indicativa de laUnesco para ser declarada  Patrimonio Cultural de la Humanidad .', '24097', '643.50 ', '3.433333333', '79.6', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (65, 7, 'Las Lajas', 'El Cantón Las Lajas es una municipalidad de la provincia de El Oro. Su cabecera cantonal es la ciudad de La Victoria. Su población es de 4.794 habitantes,1 tiene una superficie de 297km2.2 Su alcalde actual para el período 2014 - 2019 es William Valencia.', '4794', '2972.00 ', '3.785', '80.06166667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (66, 8, 'Esmeraldas', 'El cantón Esmeraldas es una entidad territorial subnacional ecuatoriana, de la Provincia de Esmeraldas. Su cabecera cantonales la ciudad de Esmeraldas, lugar donde se agrupa gran parte de su población total.', '189504', '1351.00 ', '1.05', '79.2', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (67, 8, 'Eloy Alfaro', 'El cantón Eloy Alfaro está ubicado al norte de la Provincia de Esmeraldas, en Ecuador. Su cabecera cantonal es LimonesLas principales poblaciones son Borbón, Maldonado, Selva Alegre, Playa de Oro, Atahualpa, Zapallo Grande, San Francisco del Onzole y Santo Domingo del Onzole. Estas poblaciones están distribuidas a lo largo de la cuenca del Río Santiago, donde confluyen además el Río Cayapas y el Río Onzole.', '39739', '4302.00 ', '1.25', '79', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (68, 8, 'Muisne', 'El cantón Muisne está ubicado al oeste de la Provincia de Esmeraldas, en Ecuador. Su cabecera cantonal es la parroquia urbana de Muisne. En lenguaje indígena Muisne significa abundancia.', '28474', '1265.00 ', '0.606666667', '80.01833333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (69, 8, 'Quininde', 'El cantón Quinindé es una entidad territorial subnacional ecuatoriana, de la Provincia de Esmeraldas. Se ubica en la Región Costa. Su cabecera cantonal es la ciudad de Quinindé, lugar donde se agrupa gran parte de su población total.', '122570', '3855.00 ', '0.333333333', '79.48333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (70, 8, 'San LOrenzo', 'San Lorenzo es un cantón y puerto de la provincia de Esmeraldas, Ecuador. Está ubicado en la parte más septentrional del país siendo parte del mega sistema del Chocó, región de gran biodiversidad y en la que conviven las culturas indígenas Awá, Chachi, Éperas y los afro descendientes.1', '42486', '18572.00 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (71, 8, 'Atacames', 'Atacames, es un cantón situado en la provincia de Esmeraldas, en la costa norte de Ecuador. Su capital es la ciudad de Atacames. En 2005 su población fue de 11,251 habitantes.2 siendo los afroecuatorianos un grupo muy importante junto a los descendientes de los aborígenes atacames.', '41526', '512.7 2', '0.866666667', '79.83333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (72, 8, 'Rioverde', 'El Cantón Río Verde es una municipalidad de la provincia de Esmeraldas. Su cabecera cantonal es la ciudad de Rioverde, fundada por el científico Pedro Vicente Maldonado en el año 1743. Su población es de 26.869 habitantes', '26869', '1.506 2', '1.060277778', '79.40444444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (73, 8, 'La Concordia', 'La concordia es una ciudad ecuatoriana, de la Provincia de Santo Domingo de los Tsachilas, ubicada al noroeste del país, tiene aproximadamente 29.003 habitantes en la cabecera urbana y 42.924 habitantes en todo el cantón.', '42924', '325.00 ', '0.006666667', '79.39583333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (74, 9, 'San Cristóbal', 'Su nombre oficial y en español,  San Cristóbal , proviene del patrón de los marinos, San Cristóbal (Christopher). Su antiguo nombre en inglés, chatham, provenía de William Pitt (el Viejo), primer conde de Chatham.', '5600', '558.00 ', '0.8', '89.4', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (75, 9, 'Isabella', 'El Cantón Isabela es una municipalidad de la provincia de Galápagos. Su cabecera cantonal es la ciudad de Puerto Villamil. Su población es de 2.256 habitantes,1 tiene una superficie de 5.368km2.2 Su alcalde actual para el período 2009 - 2014 es Bolívar Tupiza.', '0', '5.37 ', '0.4725', '90.68111111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (76, 9, 'Santa Cruz', '', '0', '0.00 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (77, 10, 'Guayaquil', 'El cantón Guayaquil es una entidad territorial subnacional ecuatoriana, capital de la provincia del Guayas, en la República del Ecuador. Con más de 2.350.915 habitantes, según estadísticas de 2010, es el cantón más poblado del Ecuador. Su cabecera cantonal es la ciudad de Santiago de Guayaquil, lugar donde se agrupa más del 85% de su población total.', '0', '344 5 ', '2.183333333', '79.88333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (78, 10, 'Alfredo Baquerizo Moreno', 'El cantón Alfredo Baquerizo Moreno es un entidad subnacional de la provincia del Guayas en el Ecuador. Tiene una extensión de 218 km² y su población aproximada es de 25 179 habitantes', '25 179', '218.00 ', '1.916666667', '79.51666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (79, 10, 'Balao', 'Balao es un cantón de la Provincia del Guayas en la República del Ecuador. Su cabecera cantonal es Balao. Su territorio tiene una extensión de 469 Km² y su población es de 20.523 habitantes.1', '0', '469.00 ', '2.916666667', '79.81666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (80, 10, 'Balzar', 'Balzar es un cantón del norte de la Provincia del Guayas en la República del Ecuador. Su cabecera cantonal es San Jacinto de Balzar. La cantonización de Balzar ocurrió bajo la presidencia de Leonidas Plaza Gutiérrez, en el año de 1903, durante el mes de Septiembre, cuando el día 16 el H. Congreso Nacional expide el decreto de cantonización, que se ejecuta el 26 del mismo mes.', '0', '2518.00 ', '1.366666667', '79.9', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (81, 10, 'Colimes', 'Colimes es un cantón de la Provincia del Guayas en la República del Ecuador. Su cabecera cantonal es Colimes. Su territorio abarca una extensión de 755 Km² y su población 23.423 mil habitantes.1', '0', '755.00 ', '1.55', '80.01666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (82, 10, 'Daule', 'El Cantón de Daule se encuentra localizado en el Ecuador, en región de la costa en la Provincia del Guayas, con una población 120 mil habitantes, de los cuales 65 mil viven en el área urbana, en la ciudad de Daule, y 54 mil en la zona rural, apenas 7.038 casas cuentan con una red pública de agua potable, según datos del Instituto Nacional de Estadística y Censos (INEC) en el 2001.', '120 326', '475.00 ', '1.866666667', '79.98333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (83, 10, 'Duran', 'Eloy Alfaro, mejor conocido como Durán, es un cantón de la provincia del Guayas, en el Ecuador. Está ubicado en el margen oriental del río Guayas. Está situada frente a la ciudad de Guayaquil, capital provincial, a la que está unida por el puente de la Unidad Nacional. La ciudad de Durán es la cabecera del cantón homónimo. Según datos oficiales del INEC según censo 2010, la ciudad tiene 255.769 habitantes y el cantón 235.769', '255769', '58.60 ', '2.166666667', '79.83333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (84, 10, 'El Empalme', 'El cantón El Empalme es una entidad territorial subnacional ecuatoriana, de la Provincia de Guayas. Se ubica en la Región Costa. Su cabecera cantonal es la ciudad de Velasco Ibarra, lugar donde se agrupa gran parte de su población total.', '74 451', '711.00 ', '1.046111111', '79.63361111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (85, 10, 'El Triunfo', 'El Triunfo es un cantón de la Provincia del Guayas en la República del Ecuador. Su cabecera cantonal es la ciudad de El Triunfo. Su territorio tiene una extensión de 405 km² y su población es de 50.060 mil habitantes.1', '50060', '405.00 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (86, 10, 'Milagro', 'El cantón Milagro es una entidad territorial subnacional ecuatoriana, de la provincia de Guayas. Su cabecera cantonal es la ciudad de Milagro, lugar donde se agrupa gran parte de su población total.', '166 634', '401.00 ', '2.126944444', '79.59916667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (87, 10, 'Naranjal', 'El cantón Naranjal es una entidad territorial subnacional ecuatoriana, de la Provincia de Guayas. Se ubica al sur de la Región Costa. Su cabecera cantonal es la ciudad de Naranjal, lugar donde se agrupa gran parte de su población total. Su territorio tiene una superficie de 2.015 km² y su población de 53.800 habitantes.', '69012', '2.02 ', '2.672777778', '79.615', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (88, 10, 'Naranjito', 'La población es muy antigua aunque en los años de la colonia no tuvo mayor importancia. En la República, el 21 de junio de1873 fue creada como parroquia rural del cantón Yaguachi, al que perteneció hasta el 17 de septiembre de 1913 en que al crearse el cantón Milagro pasó a pertenecer a esa nueva jurisdicción', '32963', '250.00 ', '2.173055556', '79.46722222', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (89, 10, 'Palestina', 'Palestina es un cantón de la provincia del Guayas, en la República del Ecuador. Su cabecera cantonal es Palestina. Está ubicada en el centro-norte de la provincia.', '0', '80.00 ', '1.626388889', '79.97694444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (90, 10, 'Pedro Carbo', 'El cantón debe su nombre al político guayaquileño Pedro Carbo Noboa. Nacido el 19 de marzo de 1813, hijo del Coronel José Carbo Unzueta y de la Sra. Josefa Noboa y Arteta. Pedro Carbo sirvió al Ecuador desde varios cargos públicos y privados: fue Presidente, Vicepresidente y Vocal del Concejo Municipal de Guayaquil;', '39706', '0.00 ', '1.833333333', '80.23333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (91, 10, 'Samborondón', 'Samborondón es un cantón de la provincia del Guayas, en la República del Ecuador. Se encuentra al frente de Guayaquil, separado por el Río Daule y frente al Cantón Durán, separado por el Río Babahoyo; para llegar de ambos cantones está elPuente de la Unidad Nacional y el Puente Alterno Norte.', '67 590', '252.00 ', '2.183333333', '79.88333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (92, 10, 'Santa Lucía', 'Santa Lucía es un cantón de la Provincia del Guayas en la República del Ecuador. Su cabecera cantonal es la ciudad de Santa Lucía. Tiene una extensión de 359 Km² y su población es de 38.923 habitantes que se distribuyen en 8.810 en el área urbana y 30.113 en el sector rural según el censo del INEC del 26 de Noviembre del 2010.2', '38 923', '359.00 ', '1.713888889', '79.985', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (93, 10, 'Urbina Jado', 'Salitre, antiguamente conocido como Urbina Jado, es un cantón de la provincia del Guayas, en la República del Ecuador.1 Está ubicado en el centro-oeste de la provincia. También es conocida como la  Capital montubia del Ecuador .2', '73000', '388.00 ', '1.829444444', '79.81555556', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (94, 10, 'Yaguachi', 'Yaguachi es un cantón de la provincia del Guayas de la República del Ecuador. Su cabecera cantonal es la ciudad de San Jacinto de Yaguachi.', '0', '0.00 ', '2.19', '79.8875', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (95, 10, 'Playas', 'El cantón Playas está situado al suroeste de la provincia del Guayas. Su cabecera cantonal es la ciudad de General Villamil, más conocida como  Playas . Está a 96 kilómetros de la capital de provincia Guayaquil. Limita al norte y este con los cantonesGuayaquil y Santa Elena; al sur y oeste con el Océano Pacífico.', '0', '280.20 ', '2.19', '79.8875', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (96, 10, 'Simon Bolivar', 'El Cantón Simón Bolívar, está ubicado en la Provincia del Guayas en el margen Oriental, al norte del Cantón Naranjito y al Este del Cantón Milagro, tiene una extensión aproximada de 29.273,30 Ha., distribuido en sus dos parroquias: Parroquia Urbana Simón Bolívar 13.233,70 Ha. y Parroquia Rural Lorenzo de Garaicoa 16.039,60 Ha. siendo la extensión de la cabecera cantonal 436,46 Ha.', '25483', '292.00 ', '2.156666667', '79.8875', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (97, 10, 'Coronel Marcelino Maridueña', 'Marcelino Maridueña es un cantón de la provincia del Guayas, en la República del Ecuador. Su cabecera es Marcelino Maridueña situada a 65 km de la ciudad de Guayaquil. Se encuentra al este de la provincia, asentada a 80 m.s.n.m., su temperatura promedio es de 24 °C y una precipitación promedio anual de 1700 mm.', '0', '337.00 ', '2.2', '79.41666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (98, 10, 'Lomas de Sargentillo', 'El territorio que ocupa el cantón Lomas de Sargentillo se extiende sobre un área de 533 Km². Limita al norte conel cantón Santa Lucía; al sur con los cantones Nobol, Isidro Ayora y Guayaquil; al este con los cantones Daule y Nobol, y al oeste con el cantónIsidro Ayora. Su población es de 35 000 habitantes de los cuales 1500 son urbanos y 2000 son rurales.', '0', '533.00 ', '1.883333333', '80.08333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (99, 10, 'Nobol', 'Nobol es un cantón de la Provincia del Guayas en la República del Ecuador. Su cabecera cantonal es Nobol. Tiene una extensión está cerca de los 127 Km² y su población es de 14 mil habitantes aproximadamente que se reparten En 6 407 en la población urbana y 7 769 en la población rural según el censo del INEC del 2001.en este lugar se encuentra el santuario de Santa Narcisa de Jesús que fue declarada santa en el 2008.', '14000', '127.00 ', '1.916666667', '80.7', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (100, 10, 'General Antonio Elizalde', 'El cantón Bucay está situado al este de la provincia del Guayas. Su cabecera cantonal es también conocida como Bucay. Está a 96 kilómetros de la capital de provincia Guayaquil. Limita al norte con la provincia de Bolívar; al sur y al este con la provincia deChimborazo; y al oeste con los cantones Naranjito y Marcelino Maridueña.1', '10000', '154.00 ', '2.2', '79.16666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (101, 10, 'Isidro Ayora', 'Isidro Ayora es uno de los 25 cantones de la provincia del Guayas en la República del Ecuador. Tanto el cantón como su cabecera cantonal, llevan el mismo nombre en honor al ex presidente Isidro Ramón Ayora Cueva.', '10870', '488.00 ', '1.883333333', '80.16666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (102, 11, 'Ibarra', 'El cantón Ibarra es una entidad territorial subnacional ecuatoriana, de la Provincia de Imbabura. Su cabecera cantonal es la ciudad de Ibarra, lugar donde se agrupa gran parte de su población total.', '181175', '1093.00 ', '2.908888889', '78.98277778', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (103, 11, 'Antonio Ante', 'Antonio Ante es un cantón de Ecuador, localizado en la provincia de Imbabura. Su cabecera cantonal es la parroquia de Atuntaqui. Su población en el censo de 2010 es de 45.184 habitantes. Tiene una superficie de 79km2. Densidad de 456,36hab/km2.', '45184', '79.00 ', '0.32', '78.21', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (104, 11, 'Cotacachi', 'El Cantón Cotacachi es una municipalidad de la provincia de Imbabura. Su cabecera cantonal es la ciudad de Cotacachi. Su población es de 40.036 habitantes,1 tiene una superficie de 1726km2.2 Su alcalde actual para el período 2009 - 2014 es Alberto Anrango.', '40036', '1726.00 ', '0.3', '78.26666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (105, 11, 'Otavalo', 'El cantón Otavalo es una entidad territorial subnacional ecuatoriana, de la Provincia de Imbabura. Se ubica al norte de laRegión Sierra. Su cabecera cantonal es la ciudad de Otavalo, lugar donde se agrupa gran parte de su población total.', '104 874', '500.00 ', '0.233333333', '78.26666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (106, 11, 'Pimampiro', 'Pimampiro es un cantón de la Provincia de Imbabura en Ecuador. Su nombre oficial es San Pedro de Pimampiro.', '12970', '442.50 ', '0.4', '77.96666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (107, 11, 'San Miguel de Urcuqui', 'San Miguel de Urcuquí es un cantón de Ecuador, localizado en la provincia de Imbabura. Su cabecera cantonal es la ciudad de Urcuquí. Su población en el censo de 2010 es de 15.888 habitantes. Tiene una superficie de 767km2. Altura sobre el nivel del mar 2384msnm. Alberga a la Ciudad del conocimiento Yachay, la primera ciudad planificada de Ecuador', '17301', '7672.00 ', '0.420223283', '-78.21819305', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (108, 12, 'Loja', 'El cantón Loja es un cantón en la provincia de Loja, Ecuador. El origen de su nombre se debe a la presencia de la ciudad y cabecera cantonal de Loja.', '214855', '1928.00 ', '3.983333333', '79.2', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (109, 12, 'Calvas', 'El Cantón Calvas es creado el 25 de junio de 1824 con la expedición de la Ley de División Territorial, decretada por Francisco Paula de Santander, presidente de la Gran Colombia, cuya jurisdicción comprendía los actuales cantones de: Macará, Sozoranga, Espíndola y parte Gonzanamá. Se ratifica su creación en 1830 y en 1861, pero elevando a Sozoranga como cabecera cantonal y luego en 1863 devolviendo la categoría de Cabecera Cantonal a Cariamanga.', '28185', '855.00 ', '4.326944444', '79.55444444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (110, 12, 'Catamayo', 'Catamayo es un cantón que se encuentra ubicado provincia de Loja, Ecuador. se caracteriza por maravillosos paisajes naturales en los que se destaca una flora y fauna exuberantes. Posee los valles más fértiles, de inagotable abundancia agrícola de la Región, los mismos que son bañados por dos ríos, Boquerón y Guayabal.', '30638', '649.00 ', '-4', '-79.35', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (111, 12, 'Celica', 'Esta región accidental de la provincia de Loja, de topografía plasmada de contrastes: Azuladas montañas, verdes y profundos avalles, diversos pisos climáticos, exuberante agricultura y variada ganadería, fue fundada sobre poblados indígenas aún más antiguos a los Incas.', '14468', '517.80 ', '4.103055556', '79.95611111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (112, 12, 'Chaguarpamba', 'Chaguarpamba es un cantón de la Provincia de Loja en Ecuador, su población estimada es de 7.161 habitantes.1 El Alcalde actual es el Ingeniero Líder Moises Córdova Robles. Su extensión territorial es 311.7 km², con una altitud promedio de 1050 msnm. La temperatura promedio es de 18ºC a 24ºC.', '7161', '311.70 ', '3.876944444', '79.64416667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (113, 12, 'Espindola', 'Espíndola es un cantón de la Provincia de Loja en Ecuador, su población estimada es de 14.799 habitantes.1 El alcalde actual es el Ing. Alvaro García. La fiesta de cantonización se celebra el 27 de abril. La extensión territorial es de 514.22 km2. La altura sobre el nivel del mar varía de 1400 a 3400 msnm.', '14799', '514.22 ', '4.583055556', '79.42694444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (114, 12, 'Gonzanama', 'Gonzanamá es uno de los 16 cantones que conforman la provincia de Loja, Ecuador, se encuentra ubicado en una amplia zona al sureste del cantón Loja. Se encuentra ubicado en la parte central de la provincia con dirección sur oriente; su altura fluctúa entre 1.000 y 2.800 msnm.', '12716', '0.00 ', '4.231388889', '79.43527778', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (115, 12, 'Macara', 'Ubicado a 195 Km de la ciudad de Loja, valle extenso de terrenos empleados principalmente para la producción de arroz. Es atravesado por el río del mismo nombre, formándose en sus riveras balnearios visitados por turistas nacionales y extranjeros. Este río sirve también de límite natural internacional con el Perú, sobre el cual se levanta el puente Internacional que da la bienvenida a turistas que visitan. El cantón cuenta con 21901 habitantes.', '19018', '578.00 ', '4.383333333', '79.95', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (116, 12, 'Paltas', 'Paltas es un cantón de la provincia de Loja, lleva este nombre por la presencia de la etnia preincaica Palta, que constituye el elemento fundamental de la etnografía e historia provincial. Su alcalde para el período 2014 - 2019 es Ramiro Maita Sánchez.', '23801', '0.00 ', '4.048888889', '79.6475', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (117, 12, 'Puyango', 'Puyango cantón de la provincia de Loja en Ecuador, con una superficie de 643 km², temperatura media 18ºC, altura 1380 msnm, tiene 16.800 habitantes, su alcalde actual para el período 2014 - 2019 es Carlos Patricio Granda Sánchez. Su cabecera cantonal es la ciudad de Alamor.', '15513', '643.00 ', '4.017777778', '80.02138889', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (118, 12, 'Saraguro', 'Saraguro es un cantón de la Provincia de Loja en Ecuador, su población estimada es de 30.183 habitantes.1 En el cantón se encuentra la nacionalidad indígena de los Saraguros.', '30183', '0.00 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (119, 12, 'Sozoranga', 'Sozoranga es un cantón de la Provincia de Loja en Ecuador, su población estimada es de 7.465 habitantes.1 El alcalde actual es el Sr. Romeo Moreno. La fiesta de cantonización se celebra el 20 de diciembre.', '7465', '0.00 ', '4.327777778', '79.78972222', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (120, 12, 'Zapotillo', 'Cantón Zapotillo, Situado en la parte occidental de la provincia de Loja, en el sur de Ecuador, cuya cabecera cantonal esZapotillo. Tiene una exuberante belleza natural rodeada de tamarindos, algarrobos y palmeras, motivo el cual es conocido como El Paraíso Escondido en la Tierra.', '12312', '1215.00 ', '4.386388889', '80.24361111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (121, 12, 'Pindal', 'Pindal es un cantón de la Provincia de Loja en Ecuador, su población estimada es de 8.645 habitantes.1 El alcalde actual es el Sr.Livar Bustamnte C.', '8645', '0.00 ', '4.116111111', '80.1075', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (122, 12, 'Quilanga', 'Canton Quilanga  es un cantón del Ecuador, ubicado en la Provincia de Loja. Su capital es la ciudad de Quilanga. Su población en el censo de 2001 era de 4.582.1 Su alcalde actual para el período 2009 - 2014 es Francisco Jaramillo.', '4337', '0.00 ', '4.298055556', '79.39861111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (123, 12, 'Olmedo', 'Es uno de los productores del mejor Café del Ecuador y del mundo, lo confirma su participación en las 6 ediciones de Taza Dorada, en las ha estado en el top de los 10 mejores cafés especiales del País, y es Bicampeón Nacional (2009, 2013).', '4870', '62.63 ', '3.936111111', '79.64611111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (124, 13, 'Baba', 'Baba nació de la Tribu conocida como  Tribu de los Babas , la misma que era conocida por transportar mercadería a las zonas cercanas, su más grande aliado, una tribu cercana que se asentaba en una localidad bodeguera, ahora convertida en la capital de la Provincia de Los Rios (Babahoyo), ambas eran extensas zonas comerciales de la región. Los Babas o Babahuyus eran guerreros de raza huancavilca que habitaban en esta región, fue con ellos que el conquistador Sebastián de Benalcazar realizó una alianza para evitar el enfrentamiento armado.', '39681', '516.00 ', '1.784166667', '79.67666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (125, 13, 'Babahoyo', 'Babahoyo es una ciudad ecuatoriana, capital de Los Ríos. Es la ciudad mas importante y la segunda ciudad mas poblada de la Provincia de Los Ríos. Es una de las ciudades más importantes del Ecuador y el puerto fluvial más importante del país. Rodeada de ríos como el río Babahoyo que se unen y desembocan en el río Guayas. Es la capital provincial más cerca al puerto principal del país, Guayaquil.', '153776', '1076.00 ', '1.816666667', '79.51666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (126, 13, 'Buena Fe', 'Buena Fe es una ciudad ecuatoriana ubicada al centro de dicho país, cuenta con una población de 38.263 habitantes (en 2010), su actividad económica principal es la agropecuaria. Es la cabecera cantonal del Cantón Buena Fe y la tercera ciudad más grande y poblada de la Provincia de Los Ríos.', '63148', '569.00 ', '0.893055556', '79.49083333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (127, 13, 'Mocache', 'Mocache es un cantón de la provincia de Los Ríos en Ecuador. Su cabecera cantonal es la ciudad de Mocache. Su población es de 38.392 habitantes,1 tiene una superficie de 572.3 km2.2 Su alcalde actual para el período 2009 - 2014 es Leandro Ullón Rodríguez.', '38392', '562.00 ', '1.190555556', '79.50611111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (128, 13, 'Montalvo', 'Montalvo es un cantón de la Provincia de Los Ríos en la región costa de Ecuador, su cabecera cantonal es Montalvo, consta de una parroquia urbana: Montalvo, y una parroquia rural: La Esmeralda, además de los recintos: La Nena, La Esmeralda, San Jorge, San Pedro, Potosí, Pisagua, las Mercedes, Las Cruces, El Triunfo, La Pradera, Limatón, Carmen Rosa, Las Balsas, La Vitalia, Pretoria, La Victoria, El Cisne, La Ernestina, San Antonio, San Joaquín entre otros. La principal actividad económica es la agricultura y turismo.', '24164', '362.00 ', '1.793888889', '79.28583333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (129, 13, 'Palenque', 'Palenque es un cantón de la provincia de Los Ríos en Ecuador. Su cabecera cantonal es la ciudad de Palenque. Su población es de 22.320 habitantes,1 tiene una superficie de 570 km2.2 Su alcalde actual para el período 2014 - 2019 es Alberto Ullon Loor. Cantón netamente agrícola', '22320', '570.00 ', '1.436666667', '79.75583333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (130, 13, 'Puebloviejo', 'San Francisco de Puebloviejo es un cantón de la Provincia de Los Ríos en Ecuador.Comprende la parroquia urbana de Puebloviejo (13.376 hab.) y las parroquias rurales de Puerto Pechiche (4.674 hab.) y San Juan (18.427 hab.), esta última la más poblada del cantón.', '46477', '336.00 ', '1.553611111', '79.53583333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (131, 13, 'Quevedo', 'Quevedo es una ciudad ecuatoriana ubicada al centro de dicho país, cuenta con una población de 150.827 habitantes (en 2010), su actividad económica principal es la agropecuaria. Es la cabecera cantonal del Cantón Quevedo y la ciudad más grande y poblada de la Provincia de Los Ríos.', '173575', '303.00 ', '1.033333333', '79.45', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (132, 13, 'Quinsaloma', 'Es un cantón netamente agrícola donde se produce y comercializan varios productos como: café, cacao, maíz, arroz, soya, maracuyá, fréjol de palo, banano y cítricos. Ubicado en el centro del Ecuador con un clima subtropical que estimula a propios y extraños a nadar en sus diferentes ríos que son fuentes de agua dulce que existen en sus alrededores. Quinsaloma es uno de los cantones mas jóvenes del Ecuador ya que fue creado el 20 de noviembre del 2007, mediante publicación en el Registro Oficial Nº 215. Previamente había pertenecido a Ventanas, en calidad de parroquia desde el 15 de noviembre del año 1979 hasta la fecha de cantonización.', '16476', '280.00 ', '1.206111111', '79.31444444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (133, 13, 'Urdaneta', 'Urdaneta es un cantón de la provincia de Los Ríos en Ecuador. Su cabecera cantonal es la ciudad de Catarama. Su población es de 29.263 habitantes1 y tiene una superficie de 290 km2.2 Su actual alcalde es Emilio Subía Vera.', '29263', '377.00 ', '1.570555556', '79.47111111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (134, 13, 'Valencia', 'Valencia, considerado como el  Jardín de Los Ríos , es uno de los 13 cantones más jóvenes de la Provincia de Los Ríos, del Ecuador, está localizado en la región litoral del país, a 20 minutos de Quevedo, a 5 horas de la capital Quito y a 3 horas de Guayaquil, entre las coordenadas 0°57‘09‘’ de latitud sur y 79°21‘11‘’ de longitud oeste, segundo en extensión, con 987.00 Km2.1', '42556', '707.00 ', '0.9525', '79.35305556', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (135, 13, 'Ventanas', 'Ventanas es una ciudad ecuatoriana, cabecera del Cantón Ventanas, Provincia de Los Ríos, localizada en la región litoral de Ecuador, cuenta con una población aproximadamente de 38.168 habitantes; se encuentra a dos horas de Guayaquil y a seis horas de Quito la capital del Ecuador. Está dividida en dos parroquias urbanas: Ventanas (centro-sur) y 10 de Noviembre (norte).', '42556', '288.00 ', '1.45', '79.46666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (136, 13, 'Vinces', 'San Lorenzo de Vinces es cabecera cantonal del cantón Vinces. Es uno de los cantones más antiguos de la provincia de Los Ríos, en el Ecuador con un estimado de 72.000 habitantes, el 61% de su población reside en el área rural, se caracteriza por ser una población de jóvenes, ya que alrededor del 44% de la población son menores de 20 años. En la cabecera cantonal Vinces residen alrededor de 25.000 habitantes. Se encuentra en la costa ecuatoriana, aproximadamente a 100 km al norte de la ciudad de Guayaquil.', '71736', '603.00 ', '1.55', '79.73333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (137, 14, '24 de Mayo', 'Veinticuatro de Mayo es un cantón de la provincia de Manabí en Ecuador, tiene una población de 28.846 habitantes.1 Su alcalde actual para el período 2014 - 2019 es el Ing. Ramón Vicente Cedeño Barberán. Su cabecera cantonal es la ciudad de Sucre.', '28846', '524.00 ', '1.278888889', '80.42', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (138, 14, 'Bolivar', 'Bolívar es una provincia del centro de Ecuador, en la cordillera occidental de los Andes. Su capital es la ciudad de Guaranda. La Provincia de Bolívar se llama así en honor al Libertador Simón Bolívar.', '40735', '537.00 ', '1.6', '79', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (139, 14, 'Chone', 'El cantón Chone es una entidad territorial subnacional ecuatoriana de la Provincia de Manabí, administrada por un municipio en sus instancias jurídiccionales. Su sede y capital es Chone, donde residen todas sus principales instituciones públicas y privadas. El cantón Chone se extiende a lo largo de toda la zona norte y septentrional de la provincia de Manabí, ocupando la mayor proporción territorial de dicha jurisdicción, ubicándose en la zona noroccidental de la región costa ecuatoriana y encontrándose situada en plena línea ecuatorial. Está dividida a nivel hemisférico por dicha principal línea paralela que la sobrevuela encima de un subsuelo productivo, húmedo y tórrido. Oficialmente consta dividida en 9 parroquias: 2 urbanas y 7 rurales.', '126491', '3017.00 ', '0.683333333', '80.1', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (140, 14, 'El Carmen', 'El cantón El Carmen es una entidad territorial subnacional ecuatoriana, de la Provincia de Manabí. Su cabecera cantonal es la ciudad de El Carmen, lugar donde se agrupa gran parte de su población total.', '89021', '1245.00 ', '1.314722222', '80.01694444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (141, 14, 'Flavio Alfaro', 'Flavio Alfaro es un cantón de la provincia de Manabí en Ecuador, tiene una población de 25.004 habitantes.1 Su nombre hace honor aFlavio Alfaro, político y militar liberal ecuatoriano, muerto en la Hoguera Bárbara, sobrino del General Eloy Alfaro.', '25004', '1343.00 ', '0.405833333', '79.90861111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (142, 14, 'Jama', 'Jama es un cantón de la provincia de Manabí en Ecuador, tiene una población de 23.253 habitantes.1 Su cabecera cantonal es la ciudad de Jama. El alcalde actual para el período 2009 - 2014 es Alex Cevallos Medina. La mayoría de la población se dedica al turismo, ya que posee playas por ubicarse en la costa del oceáno Pacífico.', '23253', '575.00 ', '0.2025', '80.26638889', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (143, 14, 'Jaramijo', 'Jaramijó es una cantón de la provincia de Manabí, en la Costa del Océano Pacífico, conocido como caleta de pescadores limita Al Norte con el Océano Pacífico, al Sur con Montecristi, al este con Portoviejo y al Oeste con Manta.', '18486', '97.00 ', '0.948611111', '80.63638889', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (144, 14, 'Jipijapa', 'Jipijapa, La Villa de San Lorenzo de Jipijapa, también Xipixapa. Es un cantón ubicado al sur de la provincia de Manabí. En la franja costera del Ecuador.', '71083', '347.00 ', '1.333333333', '80.58333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (145, 14, 'Junin', 'Junín es un cantón de la provincia de Manabí en Ecuador, tiene una población de 18.942 habitantes.1 Su alcalde actual para el período 2014-2019 Ing. Kleber Miguel Solorzano Villavicencio, hijo del ya fallecido ex Alcalde de Junin por 2 periodos y Diputado de la Republica del Ecuador Sr. Kettier Solorzano Jaramillo y la Sra. Mariana Villavicencio Nieto. Su fecha de cantonización fue el 8 de noviembre de1952.', '18942', '246.00 ', '0.926111111', '80.20527778', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (146, 14, 'Manta', 'Manta, denominada oficialmente como San Pablo de Manta, es una de las ciudades más importantes del Ecuador, localizada en la provincia de Manabí, es la cabecera cantonal del cantón homónimo. Está asentada en una espléndida bahía, que le ha dado la característica de puerto internacional en la costa del Océano Pacífico. La ciudad es el centro financiero y económico de la provincia, además de ser la ciudad más poblada de la provincia, y una de las principales ciudades del país, alberga grandes organismos culturales, financieros, administrativos y comerciales.', '226477', '309.00 ', '0.95', '80.71611111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (147, 14, 'Montecristi', 'Montecristi es un cantón de la provincia de Manabí en Ecuador, está ubicado al sur oriente de la provincia manabita. Entre sus mayores atractivos se encuentran la Isla de la Plata, famosa por los avistamientos de ballenas.', '70294', '734.00 ', '1.033333333', '80.65', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (148, 14, 'Olmedo', 'Olmedo es uno de los últimos cantones creados en las provincia de Manabí, el inicio fue recinto de las entonces parroquias de Santa Ana del cantón Portoviejo, al crear el cantón,Santa Ana paso a hacer parroquia Rural,hasta que el 31 de agosto de 1994 el Honorable Congreso Nacional publica en el Registro Oficial la cantonizacion de Olmedo. Las fuentes de riqueza de Olmedo son la agricultura y la ganadería. Tiene una población de 9.844 habitantes. El alcalde actual es Jacinto Zamora Rivera. 1', '9844', '253.00 ', '1.396111111', '80.20611111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (149, 14, 'Pajan', 'Paján es un cantón de la provincia de Manabí en Ecuador, tiene una población de 35.192 habitantes.1 Su cabecera cantonal es la ciudad de Paján. El alcalde actual para el período 2014 - 2019 es Galo Borbor Flores.', '37073', '1079.00 ', '1.55', '80.43333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (150, 14, 'Pedernales', 'Pedernales es un cantón de la provincia de Manabí en Ecuador, tiene una población de 55.128 habitantes.1 Su cabecera cantonal es la ciudad de Pedernales. El alcalde actual para el período 2014 - 2019 es Gabriel Alcivar Robles. La mayoría de la población se dedica al turismo, ya que posee playas por ubicarse en la costa del oceáno Pacífico.', '55128', '1932.00 ', '0.207222222', '79.57', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (151, 14, 'Pichincha', 'Pichincha es un cantón de la Provincia de Manabí, está ubicado en el extremo oriental de Manabí, bañado por las aguas del Río Daule, cuyos márgenes es la línea divisoria de esta provincia con Guayas.', '30244', '1067.00 ', '1.047222222', '79.81861111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (152, 14, 'Portoviejo', 'El cantón Portoviejo es una entidad territorial subnacional ecuatoriana, capital de la provincia de Manabí, en la República del Ecuador. Su cabecera cantonal es la ciudad de Portoviejo, lugar donde se agrupa más del 72% de su población total.1', '280029', '968.00 ', '1.056111111', '80.45527778', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (153, 14, 'Puerto Lopez', 'Puerto López es un cantón de la provincia de Manabí en Ecuador, tiene una población de 20.451 habitantes.1 Su cabecera cantonal es la ciudad dePuerto López. El alcalde actual para el período 2014 - 2019 es Miguel Plua. El cantón es famoso por el avistamiento de ballenas. Dentro del cantón se encuentra el Parque nacional Machalilla.', '20451', '420.00 ', '1.559166667', '80.81194444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (154, 14, 'Rocafuerte', 'Rocafuerte es una ciudadad de Ecuador, cabecera cantonal del homónimo cantón de Rocafuerte, en la parte occidental del territorio ecuatoriano. El 30 de septiembre de 1852 fue elevada a la categoría de cantón, con el nombre de Rocafuerte, según decreto legislativo suscrito en Guayaquil, cuando era presidente de la Asamblea Pedro Moncayo y jefe supremo, José María Urbina. En la ciudad se encuentran las principales instituciones y organizaciones del cabildo de Rocafuerte. Tiene una superficie de 280.4 Km2 y es un territorio próspero donde se realiza la elaboración de los mejores dulces que tiene la provincia de Manabí, donde se preparan más de 300 variedades. La fama de los dulces es legendaria y su sabor inconfundible. Los alfajores, bocadillos, dulce de higo, de guineo, camote, rompope y otras delicias han salvado fronteras, esto a través de los ecuatorianos en el exterior.', '33469', '280.00 ', '0.916666667', '80.46666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (155, 14, 'San Vicente', 'San Vicente es el cantón más joven de la Provincia de Manabí, creado como tal el 16 de noviembre de 1999, tras largas gestiones de los ciudadanos. Posee 2 parroquias: San Vicente, la cabecera cantonal como parroquia urbana y Canoa, como parroquia rural. Esta última es uno de los lugares preferidos de los turistas nacionales y extranjeros, debido a sus hermosas playas y sitios para visitar.', '22025', '718.00 ', '0.589166667', '80.40888889', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (156, 14, 'Santa Ana', 'Santa Ana es un cantón de la provincia de Manabí en Ecuador, tiene una población de 47.385 habitantes.1 Su alcalde actual para el período 2009 - 2014 es Ramón Cedeño. Su cabecera cantonal es la ciudad de Santa Ana de Vuelta Larga.', '47385', '1022.00 ', '1.206944444', '80.37083333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (157, 14, 'Sucre', 'Sucre es un cantón de la provincia de Manabí en Ecuador, tiene una población de 57.159 habitantes.1 Su cabecera cantonal es la ciudad de Bahía de Caráquez. El alcalde actual para el período 2009 - 2014 es Carlos Mendoza Rodríguez.', '57159', '764.00 ', '0.6', '80.41666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (158, 14, 'Tosagua', 'Tosagua es una ciudad de Ecuador, a orillas del río Carrizal, cabecera cantonal del cantón homónimo de Tosagua, situado al noroeste de la Provincia de Manabí. Su nombre se debe a la tribu nativa llamada Los Tosahuas. Fue fundada como cantón el 25 de enero de 1984 tras su aprobación por el Congreso Nacional y el presidente de la república Osvaldo Hurtado Larrea.', '38341', '377.00 ', '0.783333333', '80.26666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (159, 15, 'Gualaquiza', 'Lugar turístico ya que es rico en flora y fauna, abundante en naturaleza, encanto en cada parte y belleza arquitéctonica, calles adoquinadas originales de 1953, con una gastronomía típica del lugar, lugares turísticos tales como bomboiza, lugar de shuaras y artesanías autóctonas de la misma cultura ancestral, balnearios naturales como el ideal, entre otros de suma importancia del cantón, es comercial y una fuente de capitalización es el turismo y sus artesanías, ayudados por el comercio.', '17162', '2203.00 ', '3.383333333', '78.58333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (160, 15, 'Huamboya', 'Huamboya es un cantón de la provincia de Morona Santiago en Ecuador, tiene una población de 8.466 habitantes.1 Su cabecera cantonal es la ciudad de Huamboya. El centro cantonal se halla a una altitud de 1047msnm. Como parte de la amazonía ecuatoriana tiene un clima tropical y lluvioso casi todo el año. La alcaldesa actual es la Ing. Dominga Isabel Huambaquete Ambusha. Su fecha de cantonización es el 2 de enero de 1992.', '8466', '653.00 ', '1.785', '78.23194444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (161, 15, 'Limon Indianza', 'Se encuentra al sureste del territorio de la república del Ecuador y limita al norte con el cantón Santiago, al sur con el cantón San Juan Bosco, al este con el cantón Santiago y la República del Perú y al oeste con la provincia del Azuay, exactamente con elcantón Gualaceo de esa provincia. Se encuentra establecida a una altitud de entre 1.014 a 1.400 metros sobre el nivel del mar, aunque su cabecera cantonal se encuentra situada a una altitud de 1100 metros, su clima es templado y húmedo, manteniendo una temperatura promedio de 18 a 22 grados centígrados.', '9722', '2101.00 ', '2.95', '57.46666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (162, 15, 'Logroño', 'Logroño es un cantón de la provincia de Morona Santiago en Ecuador, tiene una población de 5.723 habitantes.1 Su cabecera cantonal es la ciudad de Logroño. Su alcalde para el período 2009-2014 es el Prof. Galo Utitiaj. Su fecha de cantonización es el 22 de enero de 1997.', '5723', '1218.00 ', '2.628333333', '78.20138889', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (163, 15, 'Morona', 'Morona es un cantón de la provincia de Morona Santiago en Ecuador, tiene una población de 41.155 habitantes.1 Su cabecera cantonal es la ciudad de Macas. Dentro del cantón se encuentra parte del Parque nacional Sangay.', '41155', '5095.00 ', '2.304444444', '78.1175', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (164, 15, 'Pablo VI', 'Pablo Sexto es un cantón de la provincia de Morona Santiago en Ecuador, tiene una población de 1.823 habitantes.1 Su cabecera cantonal es la ciudad de Pablo Sexto. Dentro del cantón se encuentra parte del Parque nacional Sangay. Su fecha de cantonización fue el 24 de octubre de 2001. Dentro del cantón se encuentra la cima del volcán Sangay a una altitud de 5280msnm. La alcaldesa actual es la Sra. Yajaira Elizabeth Ramón Rodas.', '1823', '1371.00 ', '1.928333333', '78.02722222', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (165, 15, 'Palora', 'Palora es un cantón de la provincia de Morona Santiago en Ecuador, tiene una población de 6.936 habitantes.1 Su cabecera cantonal es la ciudad de Palora.', '6936', '1436.00 ', '1.700555556', '77.96583333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (166, 15, 'San Juan Bosco', 'San Juan Bosco es una localidad en la provincia de Morona Santiago, cabecera del cantón homónimo.', '3908', '1138.00 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (167, 15, 'Santiago', 'El cantón Santiago de Méndez, más conocido como “Méndez “, es el nombre de su cabecera cantonal; se ubica en la región oriente de la provincia de Morona Santiago.', '9295', '1691.00 ', '2.716666667', '78.32222222', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (168, 15, 'Sucua', 'Sucúa es un cantón de la provincia de Morona Santiago en Ecuador, su cabecera cantonal es la ciudad de Sucúa, tiene una población de 18.318 habitantes.1 Su alcalde actual para el período 2009 - 2014 es Saúl Gilberto Cárdenas Riera.', '18318', '129815.00 ', '2.455833333', '78.1725', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (169, 15, 'Taisha', 'Taisha es un cantón de la provincia de Morona Santiago en Ecuador, tiene una población de 18.437 habitantes.1 Su cabecera cantonal es la ciudad de Taisha. Su fecha de cantonización es el 28 de junio de 1996. Su alcalde actual es el Sr. Payar Celestino Wisum Saant.', '18437', '6090.00 ', '2.383611111', '77.50638889', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (170, 15, 'Tiwintza', 'Tiwintza es uno de los doce cantones que forman la provincia de Morona Santiago, en Ecuador. Su cabecera cantonal es Santiago. Su población es de 6.995 habitantes,1 su alcalde actual para el período 2009 - 2014 es Bosco Unkuch Pujupat.', '6995', '816.00 ', '0', '0', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (171, 16, 'Archidona', 'Archidona la Bella, o Archidona es una ciudad de Ecuador, también es la cabecera cantonal del cantón Archidona. Está ubicada sobre el valle del río Misahuallí, a una altitud de 577 msnm., en la Región Amazónica del Ecuador. Archidona tiene 5478 habitantes. La ciudad es el segundo centro político de la provincia.', '24969', '3029.00 ', '0.916666667', '77.78333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (172, 16, 'Carlos Julio Arosemena', 'El Cantón Carlos Julio Arosemena Tola es una municipalidad de la provincia de Napo. Su cabecera cantonal es la ciudad de Carlos Julio Arosemena Tola. Su población es de 3.664 habitantes,1 tiene una superficie de 501km2.2 Su alcalde actual para el período 2014 - 2019 es Luis Rodrigo Caiza', '3664', '501.00 ', '1.166666667', '77.85', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (173, 16, 'El Chaco', 'El Chaco (Ecuador) es un cantón de la provincia del Napo, ubicado al norte de esta provincia. Su cabecera cantonal es la ciudad de El Chaco. Su población es de 7.960 habitantes,1 tiene una superficie de 3.473km2.2 Su alcalde actual para el período 2014 - 2019 es el Ing. Duval García.', '7960', '3473.00 ', '0.340277778', '77.80888889', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (174, 16, 'Quijos', 'El Cantón Quijos es una municipalidad de la provincia de Napo. Su cabecera cantonal es la ciudad de Baeza. Su población es de 6.224 habitantes,1 tiene una superficie de 1.557km2.2 Su alcalde actual para el período 2014 - 2019 es Javier Vinueza.', '6224', '1577.00 ', '0.463611111', '77.89277778', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (175, 16, 'Tena', 'San Juan de los dos ríos de Tena, o simplemente Tena es una ciudad de Ecuador, capital de la Región Centro Norte (Ecuador) y provincia de Napo. Además, es la cabecera cantonal del cantón Tena. Está ubicada en la zona sur de la provincia sobre el valle del río Misahuallí a una altitud de 510 msnm., en la Región Amazónica del Ecuador. Posee un clima cálido-húmedo con temperatura promedio de 25 Grado Celsius y humedad del 90 al 100 %. Tena tiene 23.307 habitantes; la ciudad está dividida en decenas de barrios. Es el centro político de la provincia, alberga los principales organismos gubernamentales, culturales y comerciales.', '60880', '3904.00 ', '0.988888889', '77.81583333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (176, 17, 'Aguarico', 'El cantón posee atractivos turísticos, debido a la importante presencia de la naturaleza. De hecho dentro de Aguarico se encuentra parte de la Reserva Natural Cuyabeno y del Parque Nacional Yasuní.', '4847', '11358.00 ', '0.921111111', '75.39916667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (177, 17, 'La Joya de los Sachas', 'Joya de los Sachas, cantón localizado en la provincia de Orellana, en el Oriente Ecuatoriano, su cabecera cantonal es la Joya de los Sachas, también conocida como Sacha o Sacha 7, es conocida así debido a la presencia de la estación petrolera Sacha 7. Sacha en idioma nativo significa selva o selva virgen. La Joya de los Sachas es la segunda ciudad más grande de la provincia de Orellana, por lo tanto la principal arteria del cantón.', '37591', '6995.00 ', '0.3', '76.85', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (178, 17, 'Loreto', 'El cantón de Loreto se encuentra ubicado en la provincia de Orellana al noroeste de la capital de Ecuador, su capital es la ciudad homónima y forma parte de la cuenca del río Napo. Se ubica en latitud Sur º44  y longitud 77º27  en un rango altitudinal que va de los 320 a 3732 msnm, en la cumbre del Volcán Sumaco.', '21163', '1195.00 ', '0.690277778', '77.30833333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (179, 17, 'Orellana', 'El cantón Francisco de Orellana es una entidad territorial subnacional ecuatoriana, de la Provincia de Orellana. Su cabecera cantonal es la ciudad de Puerto Francisco de Orellana (también conocida como El Coca), lugar donde se agrupa gran parte de su población total.', '72795', '2127.00 ', '0.466666667', '76.96666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (180, 18, 'Aranjuno', 'El Cantón Arajuno es una municipalidad de la provincia de Pastaza. Su cabecera cantonal es la ciudad de Arajuno. Su población es de 6.491 habitantes,1 tiene una superficie de 8.767km2.2 Su alcalde actual para el período 2009 - 2014 es Mario López.', '6491', '8767.00 ', '1.234166667', '77.6875', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (181, 18, 'Mera', 'Mera es una ciudad ecuatoriana; es la cabecera cantonal del Cantón Mera. Se ubica en la Región Amazónica del Ecuador, en el occidente de la Provincia de Pastaza. Es una de las ciudades más pequeñas del Ecuador.', '11861', '520.00 ', '1.370833333', '78.01944444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (182, 18, 'Pastaza', 'El cantón Pastaza es una entidad territorial subnacional ecuatoriana, de la Provincia de Pastaza. Su cabecera cantonal es la ciudad del Puyo, lugar donde se agrupa gran parte de su población total. Su población es de 62.016 habitantes,1 tiene una superficie de 19.727km2.2 Su alcalde actual para el período 2009 - 2014 es Germán Flores Mesa.', '62016', '19727.00 ', '1.066666667', '78.00111111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (183, 18, 'Santa Clara', 'El Cantón Santa Clara es una municipalidad de la provincia de Pastaza. Su cabecera cantonal es la ciudad de Santa Clara. Su población es de 3.565 habitantes,1 tiene una superficie de 311km2.2 Su alcalde actual para el período 2009 - 2014 es Víctor Hugo Verdesoto.', '3565', '311.00 ', '1.234166667', '77.6875', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (184, 19, 'Cayambe', 'El cantón Cayambe es uno de los ocho cantones de la Provincia de Pichincha. Se ubica al noreste de ella y cuenta con una superficie de 1182 km². Está conformado por ocho parroquias, tres urbanas y cinco rurales. Cayambe es a su vez la cabecera cantonal. Su cabecera cantonal es la ciudad de Cayambe, lugar donde se agrupa gran parte de su población total.', '85795', '1189.00 ', '0.043888889', '78.15611111', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (185, 19, 'Mejia', 'El cantón Mejía es una entidad territorial subnacional ecuatoriana ubicada al sur de la Provincia de Pichincha. Su cabecera cantonal es la ciudad de Machachi, lugar donde se agrupa gran parte de su población total.', '81335', '1476.00 ', '0.51', '78.56972222', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (186, 19, 'Pedro Moncayo', 'Pedro Moncayo. Uno de los 218 cantones que forman las entidades de tercer orden de de Ecuador. Está ubicado el norte de la provincia de Pichincha. Su cabecera cantonal y ciudad más grande es Tabacundo.', '33172', '332.00 ', '0.043611111', '78.34916667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (187, 19, 'Pedro Vicente Maldonado', 'El cantón Pedro Vicente Maldonado es una entidad territorial subnacional ecuatoriana ubicada al noroeste de la Provincia de Pichincha. Su cabecera cantonal es la ciudad de Pedro Vicente Maldonado, lugar donde se agrupa gran parte de su población total. Es una zona dedicada a la agricultura, ganadería y turismo ecológico, rodeada de diversos ríos y riachuelos se convierte en un atractivo para turistas nacionales e internacionales. Existen 6 cascadas denominadas: las tinas, laguna azul, cascadas verdes, el salto del tigre, Tatalá, cascada del río achotillo.', '12924', '62.00 ', '0.166666667', '79', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (188, 19, 'Puerto Quito', 'El cantón Puerto Quito es una entidad territorial subnacional ecuatoriana ubicada al noroccidente de la Provincia de Pichincha. Su cabecera cantonal es la ciudad de Puerto Quito, lugar donde se agrupa gran parte de su población total. Cuenta con atractivos destinos turísticos. Es el único cantón que no tiene frontera con el Distrito Metropolitano de Quito.', '20445', '683.00 ', '0.116944444', '79.26388889', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (189, 19, 'Quito', 'El Distrito Metropolitano de Quito es un Distrito Metropolitano de la provincia de Pichincha en el norte de Ecuador. Es la jurisdicción formada sobre la base del Municipio de la capital de la República, fundada en 1534, la ciudad de San Francisco de Quito y sus localidades cercanas, que forman una importante conurbación, sede del poder político nacional. El Distrito Metropolitano de Quito se divide en 8 administraciones zonales, las cuales contienen a 32 parroquias urbanas y 33 parroquias rurales y suburbanas. Las parroquias urbanas están divididas en barrios.', '2239191', '4183.00 ', '0.216666667', '78.5', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (190, 19, 'Rumiñahui', 'Rumiñahui es un cantón ecuatoriano, situado en la provincia de Pichincha. En este cantón se encuentra una gran parte de ElValle de Los Chillos, su capital es la ciudad de Sangolquí.', '85852', '139.00 ', '0.334444444', '78.4475', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (191, 19, 'San Miguel de los Bancos', 'San Miguel de Los Bancos es un cantón en la provincia ecuatoriana de Pichincha ubicado a 94 kilómetros de la ciudad deQuito.', '17573', '839.00 ', '0.023055556', '78.89194444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (192, 20, 'Libertad', 'La Libertad es una ciudad ecuatoriana, de la Provincia de Santa Elena. Con una población de 95 942 habitantes (en 2010), es la ciudad más poblada de Santa Elena. Se ubica en el litoral ecuatoriano, al occidente del país. Forma parte del conglomerado urbano de la puntilla de Santa Elena, junto con Salinas, Santa Elena, José Luis Tamayo y Ballenita.', '95942', '26.00 ', '2.233333333', '80.9', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (193, 20, 'Salinas', 'El cantón Salinas es una entidad territorial subnacional ecuatoriana, de la Provincia de Santa Elena. Se ubica al sur de laRegión Costa. Su cabecera cantonal es la ciudad de Salinas, lugar donde se agrupa gran parte de su población total; es el principal balneario de la provincia y uno de los más populares del país.', '68675', '97.00 ', '2.216666667', '80.95', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (194, 20, 'Santa Elena', 'El cantón Santa Elena es una entidad territorial subnacional ecuatoriana, de la Provincia de Santa Elena. Su cabecera cantonal es la ciudad de Santa Elena, lugar donde se agrupa gran parte de su población total.', '144076', '3880.00 ', '2.226666667', '80.85833333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (195, 21, 'Santo Domingo de los Colorados', 'La Provincia de Santo Domingo de los Tsáchilas es una de las provincias de la República del Ecuador y forma parte de laRegión Costa, históricamente conocida como Provincia de Yumbos. Su territorio está en zona de trópico húmedo. La provincia toma su nombre de los Tsáchilas, etnia ancestral de la zona. Su capital es la ciudad de Santo Domingo también conocida como  Santo Domingo de los Colorados  por la costumbre de los indígenas Tsáchila de pintarse el pelo de rojo con achiote.', '368013', '3860.00 ', '0.333333333', '79.25', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (196, 22, 'Cascales', 'El Cantón Cascales es una municipalidad de la provincia de Sucumbíos. Su cabecera cantonal es la ciudad de El Dorado de Cascales. Su población es de 11.104 habitantes,1 tiene una superficie de 1.248km2.2 Su alcalde actual para el período 2009 - 2014 es Enrique García Meneses. La fecha de cantonización fue el 2 de agosto de 1990.3', '11104', '1248.00 ', '0.0675', '77.21694444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (197, 22, 'Cuyabeno', 'El Cantón Cuyabeno es una municipalidad de la provincia de Sucumbíos. Su cabecera cantonal es la ciudad de Tarapoa. Su población es de 7.133 habitantes,1 tiene una superficie de 3.875km2.2 Su alcalde actual para el período 2009 - 2014 es Emilio Gaibor. Tiene un clima tropical húmedo por encontrarse en la Amazonía ecuatoriana.', '7133', '3875.00 ', '0.128888889', '76.41388889', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (198, 22, 'Gonzalo Pizarro', 'El Cantón Gonzalo Pizarro es una municipalidad de la provincia de Sucumbíos. Su cabecera cantonal es la ciudad de Lumbaquí. Su población es de 8.599 habitantes,1 tiene una superficie de 2.223km2.2 Su alcalde actual para el período 2009 - 2014 es Manuel Ramírez. El volcán Reventador se encuentra dentro del cantón. Tiene un rango de altitud de 800 a 3.600 m.s.n.m, por lo que tiene una gran variedad de climas, desde el clima tropical hasta clima de páramo.', '8599', '2223.00 ', '0.046944444', '77.32277778', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (199, 22, 'Lago Agrio', 'El cantón Lago Agrio es una entidad territorial subnacional ecuatoriana, de la Provincia de Sucumbíos. Su cabecera cantonales la ciudad de Nueva Loja, lugar donde se agrupa gran parte de su población total. Su población según el censo del 2010 es de 91.744 habitantes, de los cuales 46.966 son hombres y 44.778 mujeres.', '91744', '3139.00 ', '0.084722222', '76.88277778', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (200, 22, 'Putumayo', 'El Cantón Putumayo es una municipalidad de la provincia de Sucumbíos. Su cabecera cantonal es la ciudad de Puerto El Carmen de Putumayo. Su población es de 10.174 habitantes,1 tiene una superficie de 3.559km2.2 Su alcaldesa para el período 2014 - 2019 es Genny Ron Bustos. La fecha de cantonización fue el 30 de abril de 1969, de la provincia de Napo y desde 1989 de la provincia recién creada de Sucumbíos.3', '10174', '3559.00 ', '0.1', '75.85', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (201, 22, 'Shushufindi', 'El Cantón Shushufindi es una municipalidad de la provincia de Sucumbíos. Su cabecera cantonal es la ciudad de Shushufindi. Su población es de 44.328 habitantes,1 tiene una superficie de 2.463km2.2 Su alcalde actual para el período 2009 - 2014 es Augusto Espinoza Lema. La fecha de cantonización fue el 7 de agosto de 1984, de la provincia de Napo y desde 1989 de la provincia recién creada de Sucumbíos.3', '44328', '2463.00 ', '0.187222222', '76.645', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (202, 22, 'Sucumbios', 'Sucumbíos es una provincia del nor-oriente del Ecuador. Su capital es Nueva Loja. Es una de las principales provincias que proveen al Estado ecuatoriano del petróleo que necesita para las exportaciones. Se caracteriza por sus bellos paisajes amazónicos, entre ellos el río Aguarico.', '3390', '1502.00 ', '0.083333333', '76.88333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (203, 23, 'Ambato', 'El cantón Ambato es una entidad territorial subnacional ecuatoriana, de la Provincia de Tungurahua. Su cabecera cantonales la ciudad de Ambato, lugar donde se agrupa gran parte de su población total.', '329856', '1009.00 ', '1.241666667', '78.61972222', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (204, 23, 'Baños', 'Baños de Agua Santa o simplemente Baños es una ciudad ecuatoriana, de la Provincia de Tungurahua. Es un centro turístico de importancia del país, se encuentra a unos 45 minutos de Ambato . Es la cabecera cantonal del cantón Bañosy tiene una población de 12.995 personas. La actividad económica más importante del cantón es el turismo receptivo. La WWF le dio el nominativo a Baños de  Un Regalo para la Tierra ', '20018', '1065.00 ', '1.396388889', '78.42472222', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (205, 23, 'Cevallos', 'El cantón Cevallos está ubicado en el sector centro-sur de la Provincia de Tungurahua y al sur-oriente de la ciudad de Ambato. El nombre del cantón es en homenaje al historiador ambateño Don Pedro Fermín Cevallos.', '8163', '19.00 ', '1.35', '78.61666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (206, 23, 'Mocha', 'Mocha se deriva de la palabra Mochica “Mochoe” cuyo significado es adoratorio, se cree que este nombre se debe al Monasterio de las Vírgenes del Sol que existió en la cima del cerro Puñalica, al llegar los Incas a esta zona del país decidieron kichuizar el nombre haciendo referencia a la palabra Mucha que dentro del idioma Kichua significa beso, con el pasar del tiempo y con la mezcla entre el español y el kichua se nombró a esta tierra como Mocha.', '6777', '86.00 ', '1.416666667', '78.66666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (207, 23, 'Patate', 'El Cantón Patate es una municipalidad de la provincia de Tungurahua. Su población es de 6.720 habitantes,1 tiene una superficie de 315km2.2 El Volcán Tungurahua se encuentra dentro del cantón. Su cabecera cantonal es la ciudad de Patate.', '13497', '315.00 ', '1.316666667', '78.51666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (208, 23, 'Quero', 'El Cantón Quero es una municipalidad de la provincia de Tungurahua. Su cabecera cantonal es la ciudad de Santiago de Quero. Su población es de 19.205 habitantes,1 tiene una superficie de 173km2.2 Su alcalde actual para el período 2009 - 2014 es Raúl Gavilanes Silva.', '19205', '202.00 ', '1.379444444', '78.60694444', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (209, 23, 'San Pedro de Pelileo', 'San Pedro de Pelileo, o simplemente Pelileo, es una ciudad ecuatoriana, localizada en el centro de la Región Interandina de Ecuador. Es la capital del cantón Pelileo (56.573 habitantes según el Censo de 2010), y forma parte de laProvincia del Tungurahua.', '56573', '443.00 ', '1.330555556', '78.54277778', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (210, 23, 'Santiago de Pillaro', 'Píllaro (Santiago de Píllaro) es una localidad del centro de Ecuador, al norte de la provincia de Tungurahua. Es la cabecera del cantón Píllaro. Está localizada cerca de la ciudad de Ambato, con la que está conectada por carreteras secundarias.', '38357', '173.00 ', '1.166666667', '78.53333333', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (211, 23, 'Tisaleo', 'El Cantón Tisaleo es una municipalidad de la provincia de Tungurahua. Su población es de 12.137 habitantes,1 tiene una superficie de 59km2.2 Su alcalde actual para el período 2009 - 2014 es Rodrigo Garcés.', '12137', '60.00 ', '1.35', '78.66666667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (212, 24, 'Centinela del Cóndor', 'El cantón Centinela del Cóndor, es un cantón en la provincia de Zamora Chinchipe, Ecuador. El origen de su nombre se debe por ser una de la entradas hacia la Cordillera del Cóndor, atravesando Paquisha.', '6479', '519.00 ', '3.895', '78.78138889', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (213, 24, 'Chinchipe', 'Es el más antiguo de los cantones de Zamora Chinchipe y posee una cultura única que está en proceso de desaparición. A sus habitantes se los conoce cariñosamente comocharapanos.', '9119', '1194.00 ', '4.862777778', '79.13277778', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (214, 24, 'El Pangui', 'El cantón El Pangui, es un cantón en la provincia de Zamora Chinchipe, Ecuador. Tuvo origen en la gran tribu del pueblo Shuar que habitaron donde hoy se constituye la ciudad de El Pangui. El nombre del cantón El Pangui proviene de la terminología Shuar “PANKI” que significa boa o lugar de las boas, estos animales vivían en el inmenso remolino ubicado en las faldas del río Zamora.', '8619', '614.00 ', '3.625', '78.58722222', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (215, 24, 'Nangaritza', 'Nangaritza, es un cantón en la provincia de Zamora Chinchipe. El origen de su nombre se debe a la presencia del majestuosorío Nangaritza, que atraviesa el cantón de sur a norte. Se encuentra rodeado por los cantones Paquisha, Centinela del Cóndor,Zamora y Palanda. Al este limíta con el departamento de Amazonas, Perú, por medio de la Cordillera del Cóndor.', '5196', '2096.00 ', '4.048333333', '78.68222222', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (216, 24, 'Palanda', 'El cantón Palanda, es un cantón en la provincia de Zamora Chinchipe, Ecuador. El origen de su nombre se debe a su cabecera cantonal. Se encuentra rodeado por los cantones Zamora, Nangaritza y Chinchipe. Limita al este con el Departamento de Cajamarca, Perú y al oeste con la provincia de Loja.', '8089', '1925.00 ', '4.649722222', '79.13222222', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (217, 24, 'Paquisha', 'El cantón Paquisha, es un cantón en la provincia de Zamora Chinchipe, Ecuador. El origen de su nombre se debe a la presencia de su cabecera cantonal. Se encuentra rodeado por los cantones Yantzaza, Centinela del Cóndor, Nangaritza y al este limita con el Departamento de Amazonas, Perú, por medio de la Cordillera del Cóndor.', '3854', '261.00 ', '3.932777778', '78.67416667', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (218, 24, 'Yacuambi', 'El cantón Yacuambi, es un cantón en la provincia de Zamora Chinchipe, Ecuador. El origen de su nombre lo debe a la presencia del río Yacuambi, el cual lo atraviesa de norte a sur.', '5835', '1242.00 ', '3.634444444', '78.92805556', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (219, 24, 'Yantzaza', 'El cantón Yantzaza, es un cantón más bellos situado en la provincia de Zamora Chinchipe, Ecuador. El origen de su nombre se debe a la presencia del Valle de Yantzaza y a su cabecera cantonal. Al igual que Zamora, limita con la mayoría de cantones restantes a excepción de Chinchipe, Palanda y Nangaritza. Al norte limita con la provincia del Azuay; y al este con elDepartamento de Amazonas, Perú por medio de la Cordillera del Cóndor conocido también como el gran valle de las luciérnagas.', '18675', '990.00 ', '3.85', '78.75', 1, NULL, NULL, NULL);
INSERT INTO `cities` VALUES (220, 24, 'Zamora', 'El cantón Zamora, es un cantón en la provincia de Zamora Chinchipe, Ecuador. El origen de su nombre se debe a la presencia de la ciudad y cabecera cantonal de Zamora. Se encuentra rodeado por la mayoría de los cantones restantes, con excepción deChinchipe, El Pangui y Paquisha. Al oeste limíta con la provincia de Loja.', '25510', '1876.00 ', '4.116666667', '78.8', 1, NULL, NULL, NULL);
